  {
    return (
      <div className="container" >

      <video id="video" autoPlay="true" loop="true">

          <source src={this.state.weather} type="video/mp4" />

      </video>

      <div class="overlay">

          <form>
              <div className="aboveSubmit">
              <span>Enter city name</span>
              <input type="text" value={this.state.city}></input>
              <span>Enter country name</span>
              <input type="text" value={this.state.country}></input>
              </div>
              <button type="submit">Submit</button>

          </form>

      </div>

      </div>


    )
  }
}

export default App;
